using System;
using System.Collections.Generic;
using System.IO;
using System.Text;
using CrossCutting.Core.Pdf;

namespace CrossCutting.PdfHelper
{
    public class PdfFooterContentFactory : IPdfFooterContentFactory
    {

        public IPdfFooterContent GetPdfFooterContent(PdfFooterContentType footerContentType)
        {
            return footerContentType switch
            {
                PdfFooterContentType.String => new PdfFooterTextContent(),
                PdfFooterContentType.Image => new PdfFooterImageContent(),
                PdfFooterContentType.Line => new PdfFooterGeometryContent(),
               
                _ => throw new ArgumentOutOfRangeException(nameof(footerContentType), footerContentType, null)
            };
        }

        public static IPdfFooterTextContent PageNumberContent => new PdfFooterTextContent
        {
            Content = "Page [{0}] of [{1}]",
            ContentType = PdfFooterContentType.String,
            FontFamilyName = "DejaVuSans",
            LineAlignment = PdfFooterLineAlignment.Far,
            TextAlignment = PdfFooterTextAlignment.Center,
            SizeEm = 12,
            Color = PdfCustomColors.DarkGray,
            InflatePositionBoxWidth =  -30,
            InflatePositionBoxHeight = -35
        };

        public static IPdfFooterTextContent PatientNameContent { get; set; } = new PdfFooterTextContent
        {
            Content = "[{0}] • Visit History Record",
            ContentType = PdfFooterContentType.String,
            TextAlignment = PdfFooterTextAlignment.Left,
            LineAlignment = PdfFooterLineAlignment.Far,
            FontFamilyName = "DejaVuSans",
            SizeEm = 12,
            Color = PdfCustomColors.DarkGray,
            InflatePositionBoxWidth = -30,
            InflatePositionBoxHeight = -35
        };

        public static IPdfFooterTextContent PoweredByDoctoriaContent { get; set; } = new PdfFooterTextContent
        {
            Content = @"Powered by Doctoralia",
            ContentType = PdfFooterContentType.String,
            UseBoxPosition = false,
            FontFamilyName = "DejaVuSans",
            SizeEm = 12,
            Color = PdfCustomColors.DarkGray,
            InflatePositionBoxWidth = -30,
            InflatePositionBoxHeight = -35
        };

        public static IPdfFooterGeometryContent LineSeparator { get; set; } = new PdfFooterGeometryContent
        {
            ContentType = PdfFooterContentType.Line,
            Color = PdfCustomColors.LightGray,
            InflatePositionBoxWidth = -30,
            InflatePositionBoxHeight = -60
        };

        public static IPdfFooterImageContent LogoContent { get; set; } = new PdfFooterImageContent()
        {
            ContentType = PdfFooterContentType.Image,
            Content = GetDocPlannerLogo50X48Blue(),
            UseBoxPosition = false,
            InflatePositionBoxWidth = -30,
            InflatePositionBoxHeight = -35
        };

        private static byte[] GetDocPlannerLogo50X48Blue()
        {
            var doctoralia50x48BlueLogoString =
                "";
            var logoStream = GetStreamFromBase64(doctoralia50x48BlueLogoString);

            byte[] bytes = new byte[logoStream.Length];
            logoStream.Read(bytes, 0, (int)logoStream.Length);

            return bytes;
        }

        private static Stream GetStreamFromBase64(string base64EncodedString)
        {
            var bytes = Convert.FromBase64String(base64EncodedString);
            return new MemoryStream(bytes);
        }
    }
}
